import logo from './logo.svg';
import './App.css';
import Card from './card';
import "../node_modules/bootstrap/dist/css/bootstrap.min.css";
import { useState } from 'react';

function App() {
  const[counter,Setcounter]=useState([1]);
  function foo(counter){
    // div.innnerHtml=`<div style="width:100px;height:20px;backgroundcolor:wheat;top:20px;text-align:center;">
    // ${counter.length}</div>`;
    Setcounter([...counter,{count:counter.length+1}]);
    <div style={{
      width:"100px",
      height:"20px",
      backgroundColor:"wheat",
      top:"20px",
      textAlign:"center"
    }}>{counter.length}
    </div>
  }
  return (
    <>
      <div className='App'>
        <button onClick={foo()}>ADD</button>
      </div>
    </>
  );
}

export default App;
